<?php session_start();	if (!isset($_POST['nom']) OR !isset($_POST['prenom']) OR !isset($_POST['sexe'])) {		if (!isset($_SESSION['nbr'])) {			$_SESSION['nbr'] = 0;		}		else {			$_SESSION['nbr'] = $_SESSION['nbr'] + 1;			echo "Cela fait " . $_SESSION['nbr'] . " fois que tous les champs ne sont pas remplis!<br/>";		}	}	else {		echo "Bonjour " . $_POST['prenom'] . " " . $_POST['nom'] . " <br/>";		$result = "";		if ($_POST['sexe'] == "F") {			$result = "une femme";		}		else {			$result = "un homme";		}		echo "Vous Ãªtes {$result}";		session_destroy();	}?>	<!DOCTYPE html><html>	<head>		<meta charset="utf-8">		<title>Bonjour</title>	</head>	<body>			<form method="post" action="exemple.php">			<label> Nom : </label>			<input type="text" name="nom"/><br/>			<label> Prenom : </label>			<input type="text" name="prenom"/><br/>			<label> Sexe :</label>			<input type="radio" name="sexe" value="F"/>Femme			<input type="radio" name="sexe" value="H"/>Homme<br/>			<input type="submit" value="Envoyer"/>		</form>							</body></html>